<template>
  <section id="login">
    <form @submit.prevent="submit">
      <user-message v-if="userMessage" :message="userMessage"></user-message>
      <div :class="{spinning: loading}">
	<div class="form-block">
	  <label for="email">Email</label>
	  <input v-model.trim="email" class="field" name="email" type="email" placeholder="you@youremail.com" required>
	</div>
	<div class="form-block">
	  <label for="password">Password</label>
	  <input v-model.trim="password" class="field" name="password" type="password" placeholder="*********" required>
	</div>
	<div class="form-block">
	  <label>New User?<input v-model="newUser" type="checkbox" value="new-user"></label>
	</div>
	<div class="form-block">
	  <button>{{ newUser ? "Register" : "Login" }}</button>
	</div>
      </div>
    </form>
    </section>
</template>

<script>

import store from "../store"
import router from "../router"
import UserMessage from "./base/UserMessage.vue"

export default {
    name: "profile",
    components: {
	UserMessage
    },
    data () {
	return {
	    userMessage: null,
	    loading: false,
	    email: '',
	    newUser: false,
	    password: ''
	}
    },
    beforeCreate () {
	if (store.state.loggedIn) {
	    router.push("/profile")
	}
    },
    methods: {
	submit () {
	    this.userMessage = null;
	    this.loading = true;
	    if (this.newUser) {
		this.register()
	    } else {
		this.login()
	    }
	},
	register () {
	    let vm = this;
	    this.$http.post('/api/users', {
		email: this.email,
		password: this.password
	    }).then(function (r) {
		vm.loading = false
		vm.newUser = false
		console.log("success", r)
		vm.userMessage = {text: "User created!"}
		window.setTimeout(function () {
		    vm.userMessage = null
		}, 2000);

	    }).catch(function (e) {
		vm.loading = false
		vm.userMessage = {text: "registration failed :( " + e.response.statusText,
				  isError: true}
		window.setTimeout(function () {
		    vm.userMessage = null
		}, 2000);
	    })

	},
	login (e) {
	    let vm = this;
	    this.$http.post('/api/auth', {
		email: this.email,
		password: this.password
	    }).then(function (r) {
		vm.loading = false
		console.log("success", r);
		store.commit('LOGIN', {
		    email: vm.email,
		    access_token: r.data.access_token
		})
		router.push("/profile")
	    }).catch(function (e) {
		vm.loading = false
		vm.userMessage = {text: "login failed :( " + e.response.statusText ,
				  isError: true}
		window.setTimeout(function () {
		    vm.userMessage = null
		}, 2000);

	    })
	}
    }
}

</script>


<style lang="scss" type="text/scss">
.spinning {
    position: relative;
    transition-duration: .3s;
    > * {
        opacity: .25;
    }
    &:before {
        content: '';
        height: 100%;
        left: 0;
        position: absolute;
        top: 0;
        width: 100%;
        z-index: 9;
    }
    &:after {
        background: {
            image: url(data:image/svg+xml;base64,);
            position: center;
            size: cover;
        }
        content: '';
        height: 64px;
        left: 50%;
        position: absolute;
        top: 50%;
        transform: translate(-50%, -50%);
        width: 64px;
    }
}


section#login {
    justify-content: center;
}

#login form{
    box-shadow: 0px 0px 15px -5px black;
    border-radius: 5px;
    background-color: #F1F8E9;
    display: flex;
    align-self: center;
    align-items: center;
    padding: 2vh 2vw;
    flex-direction: column;
    width: 100vw;
    justify-content: center;
    width: 50vw;
}


</style>
