<template>
  <section id="login">
    <form class="login" @submit.prevent="submit">
      <user-message v-if="userMessage" :message="userMessage"></user-message>
      <div :class="{loading: loading}">
	<div class="form-block">
	  <input v-model.trim="email" class="field" name="email" type="email" placeholder="Email" required>
	</div>
	<div class="form-block">
	  <input v-model.trim="password" class="field" name="password" type="password" placeholder="Password" required>
	</div>
	<div class="form-block">
	  <label>New User?<input v-model="newUser" type="checkbox" value="new-user"></label>
	</div>
	<div class="form-block">
	  <button>{{ newUser ? "Register" : "Login" }}</button>
	</div>
      </div>
    </form>
    </section>
</template>

<script>

import UserMessage from "./UserMessage.vue"
import store from "../store"
import router from "../router"

export default {
    name: "profile",
    data () {
	return {
	    userMessage: null,
	    loading: false,
	    email: '',
	    newUser: false,
	    password: ''
	}
    },
    components: {
	UserMessage
    },
    beforeCreate () {
	if (store.state.loggedIn) {
	    router.push("/profile")
	}
    },
    methods: {
	submit () {
	    this.userMessage = null;
	    this.loading = true;
	    if (this.newUser) {
		this.register()
	    } else {
		this.login()
	    }
	},
	register () {
	    let vm = this;
	    this.$http.post('/api/users', {
		email: this.email,
		password: this.password
	    }).then(function (r) {
		vm.loading = false
		vm.newUser = false
		console.log("success", r)
		vm.userMessage = {text: "User created!"}
		window.setTimeout(function () {
		    vm.userMessage = null
		}, 2000);

	    }).catch(function (e) {
		vm.loading = false
		console.log("fail", e.response);
		vm.userMessage = {text: "registration failed :( " + e.response.statusText,
				  isError: true}
		window.setTimeout(function () {
		    vm.userMessage = null
		}, 2000);
	    })

	},
	login (e) {
	    let vm = this;
	    this.$http.post('/api/auth', {
		email: this.email,
		password: this.password
	    }).then(function (r) {
		vm.loading = false
		console.log("success", r);
		store.commit('LOGIN', {
		    email: vm.email,
		    access_token: r.data.access_token
		})
		router.push("/profile")
	    }).catch(function (e) {
		vm.loading = false
		vm.userMessage = {text: "login failed :( " + e.response.statusText ,
				  isError: true}
		window.setTimeout(function () {
		    vm.userMessage = null
		}, 2000);

	    })
	}
    }
}

</script>


